(43, 1, '2017-07-07 06:14:59', '2017-07-07 06:14:59', '&lt;?php get_header(); ?&gt;\r\n&lt;div class="clearfix"&gt;&lt;/div&gt;\r\n&lt;?php hotel_melbourne_breadcrumbs(); ?&gt;\r\n&lt;div class="clearfix"&gt;&lt;/div&gt;\r\n&lt;section class="blog-section"&gt;\r\n&lt;div class="container "&gt;\r\n&lt;div class="row"&gt;\r\n&lt;div class="col-md-8"&gt;\r\n&lt;div class="blog-area" data-aos="fade-up" data-aos-duration="1500"&gt;\r\n&lt;div class="room-col"&gt;\r\n&lt;div class="overly"&gt;\r\n&lt;span&gt;\r\n&lt;a href="&lt;?php the_permalink(); ?&gt;" class="hover_thumb gallery-icon"&gt;&lt;i class="fa fa-link"&gt;&lt;/i&gt;&lt;/a&gt;\r\n&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;?php the_post();\r\n$default_img = array(''class'' =&gt; "img-responsive");\r\nif(has_post_thumbnail()) :?&gt;\r\n&lt;?php the_post_thumbnail('''',$default_img); ?&gt;\r\n&lt;?php endif; ?&gt;\r\n&lt;/div&gt;\r\n&lt;div class="srb-content"&gt;\r\n&lt;div class="blog-title"&gt;&lt;h2&gt;&lt;a href="&lt;?php the_permalink(); ?&gt;"&gt;&lt;?php the_title(); ?&gt;&lt;/a&gt;&lt;/h2&gt;&lt;/div&gt;\r\n&lt;div class="blog-post-detail"&gt;\r\n&lt;i class="fa fa-user"&gt;&lt;/i&gt;&lt;a href="&lt;?php echo esc_url(get_author_posts_url( get_the_author_meta( ''ID'' ) ));?&gt;"&gt;&lt;?php _e(''Posted by:'',''hotel-melbourne''); the_author(); ?&gt;&lt;/a&gt;\r\n&lt;i class="fa fa-calendar"&gt;&lt;/i&gt;&lt;a href="&lt;?php the_permalink();?&gt;"&gt;&lt;?php _e(''Posted on:'',''hotel-melbourne''); echo get_the_date(''M j,Y''); ?&gt;&lt;/a&gt;\r\n&lt;div class="blog-tags"&gt;\r\n&lt;i class="fa fa-tags"&gt;&lt;/i&gt;&lt;a href="#"&gt;&lt;?php the_tags('''', '', ''); ?&gt;&lt;/a&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div class="blog-info"&gt;\r\n&lt;div class="blog-detail-description"&gt;\r\n&lt;p&gt;&lt;?php the_content(); ?&gt;&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;!-----user-------------------&gt;\r\n&lt;article class="blog-author" data-aos="fade-up" data-aos-duration="1500"&gt;\r\n&lt;div class="media "&gt;\r\n&lt;div class="pull-left"&gt;\r\n&lt;?php echo get_avatar( get_the_author_meta( ''user_email'' ),''class="img-responsive img-circle"'' ); ?&gt;\r\n\r\n&lt;/div&gt;\r\n&lt;div class="media-body"&gt;\r\n&lt;h6&gt;&lt;?php the_author_link(); ?&gt;&lt;/h6&gt;\r\n&lt;p&gt;&lt;?php echo get_the_author_meta( ''description'' );if(!get_the_author_meta(''description'')) _e(''No description.\r\nPlease update your profile.'',''hotel-melbourne''); ?&gt;&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/article&gt;\r\n&lt;?php comments_template( '''', true );?&gt;\r\n&lt;/div&gt;\r\n&lt;?php get_sidebar(); ?&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/section&gt;\r\n&lt;div class="clearfix"&gt;&lt;/div&gt;\r\n&lt;?php get_footer(); ?&gt;', 'Contact Us', '', 'inherit', 'closed', 'closed', '', '16-revision-v1', '', '', '2017-07-07 06:14:59', '2017-07-07 06:14:59', '', 16, 'http://localhost/wordpress-4.7.2/2017/07/07/16-revision-v1/', 0, 'revision', '', 0),
